mapper.select:
SELECT COUNT(*)
FROM SKILL
WHERE EMP_CD = #{empCd}

mapper.insert:
INSERT INTO SKILL (
	EMP_CD, SKILL_CD, SKILL_NM, SKILL_PF, INSERT_DT, MODIFY_DT
)
VALUES (
	#{empCd}, #{skillCd}, #{skillNm}, #{skillPf}, #{insertDt}, #{modifyDt}
)

mapper.delete:
DELETE FROM SKILL
WHERE EMP_CD = #{empCd}
	AND SKILL_CD = #{skillCd}
